      args: [address],
    },
  ]);

  const results = await multicall({ contracts: calls });

  // Process batched results
  return tokenAddresses.map((address, i) =&gt; ({
    address,
    name: results[i * 3]?.result,
    symbol: results[i * 3 + 1]?.result,
    deploymentInfo: results[i * 3 + 2]?.result,
  }));
}

